import Footer from '@/components/Footer';
import RightContent from '@/components/RightContent';
import { PageLoading, SettingDrawer } from '@ant-design/pro-layout';
import React from 'react';
import { history, Link } from 'umi';
import defaultSettings from '../config/defaultSettings';
import Content from './components/Content';
import { currentUser as queryCurrentUser } from './services/ant-design-pro/api';
const isDev = process.env.NODE_ENV === 'development';
const loginPath = '/user/login';
/** 获取用户信息比较慢的时候会展示一个 loading */
import { Inspector } from 'react-dev-inspector';
import MenuIcon from './components/MenuIcon';
const InspectorWrapper = isDev ? Inspector : React.Fragment;

const menuIcon = {
  icon: 'data:image/png;base64,',
};
export const initialStateConfig = {
  loading: <PageLoading />,
};
/**
 * @see  https://umijs.org/zh-CN/plugins/plugin-initial-state
 * */

export async function getInitialState() {
  const fetchUserInfo = async () => {
    try {
      const msg = await queryCurrentUser();
      console.log('queryCurrentUser', msg);
      return msg.data;
    } catch (error) {
      history.push(loginPath);
    }

    return undefined;
  }; // 如果不是登录页面，执行

  if (history.location.pathname !== loginPath) {
    const currentUser = await fetchUserInfo();
    console.log('queryCurrentUser', currentUser);
    return {
      fetchUserInfo,
      currentUser,
      settings: defaultSettings,
    };
  }

  return {
    fetchUserInfo,
    settings: defaultSettings,
  };
} // ProLayout 支持的api https://procomponents.ant.design/components/layout

export const layout = ({ initialState, setInitialState }) => {
  return {
    rightContentRender: () => <RightContent />,
    disableContentMargin: false,
    // waterMarkProps: {
    //   content: initialState?.currentUser?.name,
    // },
    menuItemRender: (item, defaultDom) => {
      // console.log('menuItemRender', item);

      console.log(item);
      if (item.pro_layout_parentKeys.length == 0) {
        return (
          <Link to={item.path}>
            <span className=" anticon custom-icon">
              <MenuIcon imgSrc={menuIcon.icon} />
              <span className="ant-pro-menu-item-title">{item.name}</span>
            </span>
          </Link>
        );
      } else {
        return (
          <Link to={item.path}>
            <span>{defaultDom}</span>
          </Link>
        );
      }
    },
    subMenuItemRender: (item, defaultDom) => {
      if (item.pro_layout_parentKeys.length == 0) {
        return (
          <span className=" anticon custom-icon">
            <MenuIcon imgSrc={menuIcon.icon} />
            <span className="ant-pro-menu-item-title">{item.name}</span>
          </span>
        );
      } else {
        return <span>{defaultDom}</span>;
      }
    },
    footerRender: () => <Footer />,
    onPageChange: () => {
      const { location } = history; // 如果没有登录，重定向到 login

      if (!initialState?.currentUser && location.pathname !== loginPath) {
        history.push(loginPath);
      }
    },
    // links: isDev
    //   ? [
    //       <Link key="openapi" to="/umi/plugin/openapi" target="_blank">
    //         <LinkOutlined />
    //         <span>OpenAPI 文档</span>
    //       </Link>,
    //       <Link to="/~docs" key="docs">
    //         <BookOutlined />
    //         <span>业务组件文档</span>
    //       </Link>,
    //     ]
    //   : [],
    menuHeaderRender: undefined,

    // 自定义 403 页面
    // unAccessible: <div>unAccessible</div>,
    // 增加一个 loading 的状态
    childrenRender: (children, props) => {
      // if (initialState?.loading) return <PageLoading />;
      return (
        <Content>
          <InspectorWrapper keys={['shift', 'c']}>
            {children}
            <SettingDrawer
              disableUrlParams
              enableDarkTheme
              settings={initialState?.settings}
              onSettingChange={(settings) => {
                setInitialState((preInitialState) => ({ ...preInitialState, settings }));
              }}
            />
          </InspectorWrapper>
        </Content>
      );
    },
    ...initialState?.settings,
  };
};
